// Get the response data
const response = $input.first().json;
const items = response.items || [];

// Track pagination info
let nextStartIndex = 1;
if (response.queries && response.queries.nextPage && response.queries.nextPage[0]) {
  nextStartIndex = response.queries.nextPage[0].startIndex;
}

// Calculate if we should continue (Google only allows up to 100 results)
const hasMoreResults = nextStartIndex <= 100;

// Process the items and include pagination info in each item
const results = items.map(item => {
  const titleParts = item.title.split(" - ");
  return {
    name: titleParts[0] || null,
    title: titleParts.slice(1).join(" - ") || null,
    link: item.link || null,
    snippet: item.snippet || null,
    image: item.pagemap?.cse_thumbnail?.[0]?.src || null,
    // Add pagination info to each item
    startIndex: nextStartIndex,
    hasMoreResults: hasMoreResults
  };
});

// If there are no results, return at least one item with pagination info
if (results.length === 0) {
  return [{ 
    json: {
      name: null,
      title: null,
      link: null,
      snippet: null,
      image: null,
      startIndex: nextStartIndex,
      hasMoreResults: false
    }
  }];
}

// Return the processed results
return results.map(r => ({ json: r }));

// Get all input items
const inputItems = $input.all();
console.log("Input items count:", inputItems.length);

// Extract pagination values with fallbacks
let nextStartIndex = 1;
let hasMoreResults = false;

// Try different approaches to find the pagination info
if (inputItems && inputItems.length > 0) {
  const firstItem = inputItems[0];

  // Check direct properties
  if (firstItem.json) {
    console.log("First item JSON:", JSON.stringify(firstItem.json));

    // Check for "index" property (instead of startIndex)
    if (firstItem.json.index !== undefined) {
      nextStartIndex = firstItem.json.index;
      console.log("Found index:", nextStartIndex);
    }

    // Check for "results" property (instead of hasMoreResults)
    if (firstItem.json.results !== undefined) {
      hasMoreResults = firstItem.json.results;
      console.log("Found results:", hasMoreResults);
    }
  }
}

// Return pagination control info
return {
  json: {
    continueLoop: hasMoreResults,
    startIndex: nextStartIndex
  }
};

