// Raw input string (replace with $json["formData"] if coming from previous node)
const rawData = "Your Full Name:Vamsi krishna, Your Email Address:Vamsivk555@gmail.com, Your Phone Number:(123) 456-7890, Billing Address:XXX New New New , Company Name:YYY, Invoice Number:21, Invoice Date:10 05 2025, Product/Service Description:Marketing, Quantity:21, Unit Price:34, Total Amount:20000";

// Split by comma first, then by colon to get key-value pairs
const fields = rawData.split(',').map(item => {
    const [key, ...rest] = item.split(':');
    const value = rest.join(':').trim(); // In case value contains ':'
    return [key.trim(), value];
});

// Convert to object
const result = Object.fromEntries(fields);

// Return as JSON for next node
return [
    {
        json: result
    }
];

