parameters.jsCode:
const editorScript = $input.first().json["Editor's Script"];
const libraryShots = $input.first().json["Library Shots Prompt"];

let formatted = "**Editorâ€™s Script:**\n\n";
formatted += editorScript + "\n\n";

formatted += "**Library Shots Prompt:**\n\n";
try {
  const shots = JSON.parse(libraryShots);
  shots.forEach(shot => {
    formatted += `Shot ${shot.shot_number} (${shot.timing}):\n`;
    formatted += `${shot.prompt}\n\n`;
  });
} catch (err) {
  formatted += libraryShots + "\n\n";
}

return [{ json: { body: formatted.trim() } }];
