// Get input data
const items = $input.all();
const standardizedLeads = [];

for (const item of items) {
  const data = item.json;

  // Generate unique Lead ID
  const leadId = `LEAD_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

  // Standardize data structure based on your form input
  const standardizedLead = {
    // Core Identity
    lead_id: leadId,
    source: data.form || 'unknown',
    created_at: data.timestamp || $now || new Date().toISOString(),

    // Contact Info
    name: data.Name || 'Unknown',
    email: data.Email || null,
    phone: data.Phone || null,
    contact_handle: data.Email || data.Phone || data.Name,

    // Lead Details
    initial_message: data.Message || '',
    initial_message_length: (data.Message || '').length,

    // Status & Scoring
    status: 'new',
    score: 0,
    qualification_status: 'not_started',

    // Tracking
    last_contacted: null,
    next_action: 'qualify',
    interaction_count: 0,

    // Metadata
    utm_source: data.utm_source || null,
    utm_campaign: data.utm_campaign || null,
    referrer: data.referrer || null,

    // Timestamps
    last_updated: new Date().toISOString()
  };

  standardizedLeads.push({ json: standardizedLead });
}

return standardizedLeads;

