for (const item of $input.all()) {
  const message = item.json.message?.content || item.json.message || "";

  // Extract key-value pairs using regex
  const extract = (key) => {
    const regex = new RegExp(`${key}:\\s*(.*)`, "i");
    const match = message.match(regex);
    return match ? match[1].trim() : "";
  };

  item.json.name = extract("Name");
  item.json.email = extract("Email");
  item.json.problem_summary = extract("Problem Summary");
  item.json.need_type = extract("Need Type");
  item.json.readiness_level = extract("Readiness Level");
  item.json.lead_score = extract("Lead Score");
  item.json.coach_notes = extract("Coach Notes");
}

return $input.all();

for (const item of $input.all()) {
  // Get the AI model output (usually inside message.content)
  const raw = item.json.message?.content || item.json.message || "";

  try {
    // Clean any formatting (like ```json or ``` from AI response)
    const clean = raw.replace(/```json|```/g, "").trim();

    // Parse JSON string into an object
    const data = JSON.parse(clean);

    // Divide all fields into separate keys
    item.json.name = data.name || "";
    item.json.email = data.email || "";
    item.json.problem_summary = data.problem_summary || "";
    item.json.need_type = data.need_type || "";
    item.json.lead_score = data.lead_score || "";
    item.json.lead_stage = data.lead_stage || "";
    item.json.reason = data.reason || "";
    item.json.coach_notes = data.coach_notes || "";

    // Store full parsed response for reference
    item.json.raw_response = data;

  } catch (error) {
    item.json.error = `Parsing failed: ${error.message}`;
  }
}

return $input.all();

