// Example input
const input = {
  text: "```json\n{\n  \"lead_status\": \"hot\",\n  \"confidence\": 95,\n  \"reasoning_summary\": \"High pain level, immediate timeline, and defined budget indicate a very serious lead ready for action.\"\n}\n```"
};

// Remove ```json wrapper
const jsonString = input.text.replace(/```json\s*|```/g, '').trim();

// Parse JSON
const leadData = JSON.parse(jsonString);

// Return as array of objects for n8n
return [
  {
    json: {
      lead_status: leadData.lead_status,
      confidence: leadData.confidence,
      reasoning_summary: leadData.reasoning_summary
    }
  }
];

const outputItems = [];

for (const item of items) {
    if (!item.json.start || !item.json.start.dateTime) continue;

    // Convert startTime from ISO string
    const startTime = new Date(item.json.start.dateTime);

    // Convert current time from $now if available, otherwise use system time
    const now = item.json.$now ? new Date(item.json.$now) : new Date();

    // Calculate difference in hours
    const diffHours = Math.round((startTime - now) / (1000 * 60 * 60));

    outputItems.push({
        json: {
            eventId: item.json.id || null,
            summary: item.json.summary || null,
            hoursUntilStart: diffHours <= 0 ? "0hr" : (diffHours === 1 ? "1hr" : `${diffHours}hrs`)
        }
    });
}

return outputItems;

// Get time value from previous node
const time = $json.time?.toLowerCase()?.trim() || "";

// Allowed time values
const validTimes = ["1hr", "2hrs", "6hrs"];

// Check if matches
const isValid = validTimes.includes(time);

// Return result
return [{ time, isValid }];

