// n8n Code Node: Split Pre-Call Brief Markdown into JSON sections (with Summary fix)

for (const item of $input.all()) {
  const content = item.json.message?.content || item.json.content || "";

  // Helper function to extract section content safely
  function extractSection(title) {
    const regex = new RegExp(`\\*\\*${title}\\*\\*[\\s\\S]*?(?=\\n\\n---|$)`, "i");
    const match = content.match(regex);
    if (!match) return "not available";

    return match[0]
      .replace(new RegExp(`\\*\\*${title}\\*\\*`, "i"), "")
      .replace(/^-+|\*+|\n+/g, "")
      .trim();
  }

  // Extract each section
  const Lead_Snapshot = extractSection("1\\. Lead Snapshot");
  const Conversation_Summary = extractSection("2\\. Conversation Summary");
  const Problem_Goal = extractSection("3\\. Problem / Goal");
  const Context_Insights = extractSection("4\\. Context & Insights");
  const Preparation_for_Coach = extractSection("5\\. Preparation for Coach");
  const Smart_Questions = extractSection("6\\. Smart Questions to Ask");
  const Recommended_Next_Step = extractSection("7\\. Recommended Next Step");

  // FIX: Capture the Summary section at the end (even if not separated by ---)
  let Summary = "not available";
  const summaryMatch = content.match(/\*\*Summary:\*\*[\s\S]*$/i);
  if (summaryMatch) {
    Summary = summaryMatch[0]
      .replace(/\*\*Summary:\*\*/i, "")
      .trim();
  }

  // Structured JSON output
  item.json = {
    Lead_Snapshot,
    Conversation_Summary,
    Problem_Goal,
    Context_Insights,
    Preparation_for_Coach,
    Smart_Questions,
    Recommended_Next_Step,
    Summary
  };
}

return $input.all();

// Split the Lead Snapshot into structured fields

for (const item of $input.all()) {
  const snapshot = item.json.Lead_Snapshot || "";

  // Extract key fields using regex
  const name = snapshot.match(/Name:\s*(.*?)(?=\s*- Email|$)/i)?.[1]?.trim() || "";
  const email = snapshot.match(/Email:\s*(.*?)(?=\s*- Date|$)/i)?.[1]?.trim() || "";
  const date = snapshot.match(/Date:\s*(.*?)(?=\s*- Lead Stage|$)/i)?.[1]?.trim() || "";
  const leadStage = snapshot.match(/Lead Stage:\s*(.*?)(?=\s*- Lead Score|$)/i)?.[1]?.trim() || "";
  const leadScore = snapshot.match(/Lead Score:\s*(.*?)(?=\s*- Need Type|$)/i)?.[1]?.trim() || "";
  const needType = snapshot.match(/Need Type:\s*(.*)/i)?.[1]?.trim() || "";

  // Add structured fields to JSON
  item.json = {
    ...item.json,
    Name: name,
    Email: email,
    Date: date,
    Lead_Stage: leadStage,
    Lead_Score: leadScore,
    Need_Type: needType
  };
}

return $input.all();
