for (const item of $input.all()) {
  const message = item.json.message?.content || item.json.message || "";

  // Extract key-value pairs using regex
  const extract = (key) => {
    const regex = new RegExp(`${key}:\\s*(.*)`, "i");
    const match = message.match(regex);
    return match ? match[1].trim() : "";
  };

  item.json.name = extract("Name");
  item.json.email = extract("Email");
  item.json.problem_summary = extract("Problem Summary");
  item.json.need_type = extract("Need Type");
  item.json.readiness_level = extract("Readiness Level");
  item.json.lead_score = extract("Lead Score");
  item.json.coach_notes = extract("Coach Notes");
}

return $input.all();

for (const item of $input.all()) {
  // Get the AI model output (usually inside message.content)
  const raw = item.json.message?.content || item.json.message || "";

  try {
    // Clean any formatting (like ```json or ``` from AI response)
    const clean = raw.replace(/```json|```/g, "").trim();

    // Parse JSON string into an object
    const data = JSON.parse(clean);

    // Divide all fields into separate keys
    item.json.name = data.name || "";
    item.json.email = data.email || "";
    item.json.problem_summary = data.problem_summary || "";
    item.json.need_type = data.need_type || "";
    item.json.lead_score = data.lead_score || "";
    item.json.lead_stage = data.lead_stage || "";
    item.json.reason = data.reason || "";
    item.json.coach_notes = data.coach_notes || "";

    // Store full parsed response for reference
    item.json.raw_response = data;

  } catch (error) {
    item.json.error = `Parsing failed: ${error.message}`;
  }
}

return $input.all();

const output = [];

for (const item of $input.all()) {
  try {
    // Extract AI message text
    const rawText = item.json.content?.parts?.[0]?.text || "";

    // Remove markdown formatting (```json ... ```)
    const cleanText = rawText.replace(/```json|```/g, "").trim();

    // Parse JSON safely
    const parsed = JSON.parse(cleanText);

    // Push cleaned fields
    output.push({
      json: {
        name: parsed.name || "",
        email: parsed.email || "",
        problem_summary: parsed.problem_summary || "",
        need_type: parsed.need_type || "",
        readiness_level: parsed.readiness_level || "",
        lead_score: parsed.lead_score || "",
        coach_notes: parsed.coach_notes || ""
      }
    });

  } catch (error) {
    // Error handler for malformed JSON
    output.push({
      json: {
        error: error.message,
        raw: item.json
      }
    });
  }
}

return output;

for (const item of $input.all()) {
  try {
    // Step 1: Extract from message.content or message
    let raw =
      item.json.message?.content ||
      item.json.message ||
      item.json.content?.parts?.[0]?.text ||
      "";

    // Step 2: Convert escaped \n to real newlines & remove markdown wrappers
    let clean = raw
      .replace(/\\n/g, "\n") // convert escaped newlines
      .replace(/```json|```/g, "") // remove code fences
      .trim();

    // Step 3: Extract only the JSON object between { and }
    const jsonMatch = clean.match(/{[\s\S]*}/);
    if (!jsonMatch) throw new Error("No valid JSON object found");
    clean = jsonMatch[0];

    // Step 4: Parse safely
    let data;
    try {
      data = JSON.parse(clean);
    } catch (err) {
      throw new Error(`JSON parse failed ‚Äî malformed or incomplete JSON`);
    }

    // Step 5: Output structured fields
    item.json = {
      name: data.name || "",
      email: data.email || "",
      snippet: data.snippet || "",
      problem_summary: data.problem_summary || "",
      need_type: data.need_type || "",
      lead_score: data.lead_score || "",
      lead_stage: data.lead_stage || "",
      reason: data.reason || "",
      coach_notes: data.coach_notes || "",
      parse_status: "success",
      raw_text: raw
    };

  } catch (error) {
    item.json = {
      error: `Parsing failed: ${error.message}`,
      raw_text: item.json.message?.content || "",
      parse_status: "failed"
    };
  }
}

return $input.all();

for (const item of $input.all()) {
  let raw = item.json.output || "";

  // Clean response text
  raw = raw.replace(/```json|```/g, "").replace(/\n/g, " ").trim();

  // Safe JSON parsing
  let data = {};
  try {
    data = JSON.parse(raw);
  } catch (e) {
    item.json.error = "Invalid JSON format";
    continue;
  }

  // Helper function to clean strings
  const clean = (str) => (str || "").toString().replace(/\n/g, " ").trim() || "Unknown";

  // Assign top-level fields
  item.json.reply = clean(data.reply);
  item.json.intent = clean(data.intent);
  item.json.confidence = clean(data.confidence);
  item.json.next_action = clean(data.next_action);

  // Handle lead_summary safely
  const summary = data.lead_summary || {};
  item.json.problem_summary = clean(summary["Problem Summary"]);
  item.json.need_type = clean(summary["Need Type"]);
  item.json.readiness_level = clean(summary["Readiness Level"]);
  item.json.lead_score = clean(summary["Lead Score"]);
  item.json.coach_notes = clean(summary["Coach Notes"]);

  // Fallback in case JSON is incomplete
  if (!item.json.reply) item.json.reply = "No valid reply generated";
}

return $input.all();

const rows = $input.all().map(item => item.json);

// Sort messages by row number (just in case)
rows.sort((a, b) => a.row_number - b.row_number);

// Take the last 5 messages
const lastFive = rows.slice(-5);

// Join only the message text (comma-separated)
const convo = lastFive
  .map(m => `${m.Convo || ""}`)
  .join(", ");

return [{
  json: {
    conversation_log: convo || "No previous messages",
    sender_profile_url: rows[0]?.Url || "",
    sender_id: rows[0]?.ID || ""
  }
}];

// Get all convo rows from previous node
const rows = $input.all().map(item => item.json);

// Sort by row_number (just to be safe)
rows.sort((a, b) => a.row_number - b.row_number);

// Take last 5 messages (in case there are more)
const lastFive = rows.slice(-5);

// Format them as a clean readable chat log
const conversation = lastFive
  .map((msg, i) => `Message ${i + 1}: ${msg.conversation_history}`)
  .join("\n");

return [
  {
    json: {
      sender_id: rows[0]?.sender_id || null,
      conversation_log: conversation,
      last_5_messages: lastFive
    }
  }
];

