const output = [];

for (const item of $input.all()) {
  try {
    // Extract AI message text
    const rawText = item.json.content?.parts?.[0]?.text || "";

    // Remove markdown formatting (```json ... ```)
    const cleanText = rawText.replace(/```json|```/g, "").trim();

    // Parse JSON safely
    const parsed = JSON.parse(cleanText);

    // Push cleaned fields
    output.push({
      json: {
        name: parsed.name || "",
        email: parsed.email || "",
        problem_summary: parsed.problem_summary || "",
        need_type: parsed.need_type || "",
        readiness_level: parsed.readiness_level || "",
        lead_score: parsed.lead_score || "",
        coach_notes: parsed.coach_notes || ""
      }
    });

  } catch (error) {
    // Error handler for malformed JSON
    output.push({
      json: {
        error: error.message,
        raw: item.json
      }
    });
  }
}

return output;

for (const item of $input.all()) {
  try {
    // Step 1: Extract from message.content or message
    let raw =
      item.json.message?.content ||
      item.json.message ||
      item.json.content?.parts?.[0]?.text ||
      "";

    // Step 2: Convert escaped \n to real newlines & remove markdown wrappers
    let clean = raw
      .replace(/\\n/g, "\n") // convert escaped newlines
      .replace(/```json|```/g, "") // remove code fences
      .trim();

    // Step 3: Extract only the JSON object between { and }
    const jsonMatch = clean.match(/{[\s\S]*}/);
    if (!jsonMatch) throw new Error("No valid JSON object found");
    clean = jsonMatch[0];

    // Step 4: Parse safely
    let data;
    try {
      data = JSON.parse(clean);
    } catch (err) {
      throw new Error(`JSON parse failed ‚Äî malformed or incomplete JSON`);
    }

    // Step 5: Output structured fields
    item.json = {
      name: data.name || "",
      email: data.email || "",
      snippet: data.snippet || "",
      problem_summary: data.problem_summary || "",
      need_type: data.need_type || "",
      lead_score: data.lead_score || "",
      lead_stage: data.lead_stage || "",
      reason: data.reason || "",
      coach_notes: data.coach_notes || "",
      parse_status: "success",
      raw_text: raw
    };

  } catch (error) {
    item.json = {
      error: `Parsing failed: ${error.message}`,
      raw_text: item.json.message?.content || "",
      parse_status: "failed"
    };
  }
}

return $input.all();

