const now = new Date();
const utc_now = now.toISOString().slice(0, 19).replace("T", " "); // UTC
const ist_now = new Date(now.getTime() + 5.5 * 60 * 60 * 1000)
                 .toISOString().slice(0, 19).replace("T", " "); // IST

return [
  {
    json: {
      utc_now,
      ist_now
    }
  }
];
