// Get the transcript array from input
const transcript = $input.item.json.transcript;

// Map each transcript entry to "Speaker Name: text"
const formatted = transcript.map(entry => {
  // Try to get name from multiple possible locations
  const speaker = entry.display_name || (entry.speaker && entry.speaker.display_name) || "Unknown";
  const text = entry.text || "";
  return `${speaker}: ${text}`;
});

// Return as single array
return [{ json: { conversation: formatted } }];

// Input: msg.json.conversation = array of strings like:
// ["Lumoscale: Yeah, okay.", "Lumoscale: Let's wait until the beginning of the day.", ...]

const conversation = $json["conversation"]; // Your conversation array

let cleanedTranscript = [];

conversation.forEach(line => {
    if (!line) return; // skip empty lines

    // Split speaker and text
    const parts = line.split(":");
    if (parts.length < 2) return; // skip lines without proper speaker

    const speaker = parts[0].trim();
    const speech = parts.slice(1).join(":").trim();

    // Filter out filler / meaningless lines
    const fillers = ["yeah", "huh", "okay", "hello", "come on in here", "what", "sorry to interrupt"];
    if (speech.length < 3) return; // skip very short lines
    if (fillers.some(f => speech.toLowerCase().includes(f))) return;

    cleanedTranscript.push({speaker, text: speech});
});

return [{json: {cleanedTranscript}}];

// Access previous OpenAI / Gemini node outputs
const objectionsNode = $node["Objections"].json["content"]["parts"][0]["text"];
const questionsNode = $node["Questions"].json["content"]["parts"][0]["text"];
const emotionalTriggersNode = $node["Emotional Triggers"].json["content"]["parts"][0]["text"];
const transformationStoriesNode = $node["Transformation Stories"].json["content"]["parts"][0]["text"];

// Helper function to remove ```json and ``` from OpenAI output
function cleanJSON(str) {
    if (!str) return "[]";
    return str.replace(/```json|```/g, '').trim();
}

// Parse outputs safely
let objections, questions, emotionalTriggers, transformationStories;

try {
    objections = JSON.parse(cleanJSON(objectionsNode));
} catch (err) {
    objections = [];
}

try {
    questions = JSON.parse(cleanJSON(questionsNode));
} catch (err) {
    questions = [];
}

try {
    emotionalTriggers = JSON.parse(cleanJSON(emotionalTriggersNode));
} catch (err) {
    emotionalTriggers = [];
}

try {
    transformationStories = JSON.parse(cleanJSON(transformationStoriesNode));
} catch (err) {
    transformationStories = [];
}

// Return combined data
return [
    {
        json: { 
            objections, 
            questions, 
            emotionalTriggers,
            transformationStories
        }
    }
];

// Extract the 7 slides from the big text and output each slide as its own item

const raw = $input.first().json.content.parts[0].text;

// Split slides by "### Slide"
const parts = raw.split("### Slide").slice(1);

const output = parts.map((p, index) => {
  const lines = p.trim().split("\n").filter(l => l.trim() !== "");

  // Remove the slide number text like "1: Hook..."
  lines.shift();

  const finalText = lines.join("\n");

  return {
    json: {
      slide: index + 1,
      message: finalText
    }
  };
});

return output;
